import React from 'react';
import './Node.css'
export default class Node extends React.Component {
  constructor(props) {
    super(props);
    this.children = [];
  }

  addChild(name) {
    this.children.push(new Node(name));
    return this;
  }

  render() {
    const {
      isFinish,
      isStart,
      isVisited
    } = props;
    const extraClassName = isFinish ? 'node-finish'
      : isStart ? 'node-start'
        : isVisited ? 'node-visited'
          : '';
    return (
      <div className={`node ${extraClassName}`}
        id={`loc-${this.props.location}`}
      // onMouseDown={() => onMouseDown(this.props.row, this.props.col)}
      // onMouseEnter={() => onMouseEnter(this.props.row, this.props.col)}
      // onMouseUp={() => onMouseUp(this.props.row, this.props.col)}
      ></div>
    )
  }
}
